using UnityEngine;
using System.Collections;
using System.Reflection;

public class ExtendedGUI {

    private static GUIStyle defaultWindowStyle;
    public static GUIStyle DefaultWindowStyle {
        get {
            if ( defaultWindowStyle == null ) {
                var t = typeof( GUI );
                var f = t.GetField( "s_Skin", BindingFlags.NonPublic | BindingFlags.Static );

                defaultWindowStyle = new GUIStyle( ( (GUISkin)f.GetValue( null ) ).window );
                defaultWindowStyle.padding.top = 20;
                defaultWindowStyle.contentOffset = new Vector2( 0, -13 );

                var normalBytes = System.Convert.FromBase64String( @"" );
                var normalTex = new Texture2D( 1, 1 );
                normalTex.hideFlags = HideFlags.HideAndDontSave;
                normalTex.LoadImage( normalBytes );
                defaultWindowStyle.normal.background = normalTex;

                var onNormalBytes = System.Convert.FromBase64String( @"" );
                var onNormalTex = new Texture2D( 1, 1 );
                onNormalTex.hideFlags = HideFlags.HideAndDontSave;
                onNormalTex.LoadImage( onNormalBytes );
                defaultWindowStyle.onNormal.background = onNormalTex;
            }

            return defaultWindowStyle;
        }
    }
}
